import { nanoid } from "nanoid";
import { useState } from "react";
import { Option } from "../Option/Option.tsx";
import styles from "./styles.module.css";

export const InputDataSelect = ({
  mapValues,
  setForm,
  size = 1,
  label,
  value,
}) => {
  const [input, setInput] = useState("");
  const handleChange = (e) => {
    const target = e.target.value;
    setInput(target);
    const newValue = () => {
      if (mapValues.some(el => el.NAME == target)) {
        return value.concat(target);
      }
    };
    console.log(newValue());
    // setForm()
  };
  return (
    <div className={styles.input_container}>
      <p>
        {value.map((el) => (
          <span key={nanoid()}>{el + " "}</span>
        ))}
      </p>
      <input
        list={label}
        type="text"
        name={label}
        value={input}
        onChange={handleChange}
        className={styles.form_input}
      />
      <datalist id={label}>
        {mapValues.map((el) => (
          <Option
            value={el.NAME}
            label={el.NAME}
            key={nanoid()}
          />
        ))}
      </datalist>
      <label
        htmlFor={label}
        className={styles.form_label}>
        {label}
      </label>
    </div>
  );
};
