import { nanoid } from "nanoid";
import { Option } from "../Option/Option.tsx";
import styles from "./styles.module.css";

export const InputDataSelect = ({
  mapValues,
  setForm,
  size = 1,
  label,
  value,
}) => {
  return (
    <div className={styles.input_container}>
      {/* <input list={label + "_input"} id={label} onChange={setForm} value={value} size={size}> */}
      <datalist
        id={label}
        className={styles.form_input}>
        {mapValues.map((el) => {
          return (
            <Option
              value={el.ID}
              label={el.NAME}
              key={nanoid()}
            />
          );
        })}
      </datalist>
      {/* </input> */}
      <label
        htmlFor={label}
        className={styles.form_label__select}>
        {label}
      </label>
    </div>
  );
};

