import { nanoid } from "nanoid";
import { useState } from "react";
import styles from "./styles.module.css";
import {Option} from "../../UI/Option/Option.tsx"

export const InputDataSelect = ({
  mapValues,
  setForm,
  label,
  value,
  values
}) => {
  const [input, setInput] = useState("");
  const handleChange = (e) => {
    const target = e.target.value;
    setInput(target);
    const newValue = () => {
      if (mapValues.some((el) => el === target)) {
        setInput("");
        const selectedCityId = values[target].ID;
        return value.concat(selectedCityId);
      } else {
        return value;
      }
    };
    setForm(newValue());
  };
  const aviableCities = value.map(id => values[id].NAME)
  console.log(aviableCities)

  return (
    <div className={styles.input_container}>
      <input
        list={label}
        type="text"
        name={label}
        value={input}
        onChange={handleChange}
        className={styles.form_input}
      />
      <datalist id={label}>
        {aviableCities.map((el) => (
          <Option
            value={el}
            label={el}
            key={nanoid()}
          />
        ))}
      </datalist>
      <label
        htmlFor={label}
        className={styles.form_label}>
        {label}
      </label>
    </div>
  );
};
