    //   return (
    //     <tr
    //       className={classNames(styles.table_row, getStyleByLeftMonths())}
    //       onDoubleClick={() => navigate(ROUTES.objects + `/${object.IDSRV}`)}>
    //       <UserData
    //         data={data}
    //         selectedRow={selectedRow}
    //       />
    //     </tr>
    //   );
    // }
    // else return null
  });

  // return (
  //   <tr
  //     className={classNames(styles.table_row, getStyleByLeftMonths())}
  //     onDoubleClick={() => navigate(ROUTES.objects + `/${object.IDSRV}`)}>
  //     <UserData
  //       data={data}
