import { nanoid } from "nanoid";
import { useState } from "react";
import styles from "./styles.module.css";
import { Option } from "../../UI/Option/Option.tsx";

export const InputDataSelect = ({ mapValues, setForm, label, value }) => {
  const [input, setInput] = useState("");
  const handleChange = (e) => {
    const target = e.target.value;
    setInput(target);
    const newValue = () => {
      if (mapValues.some((el) => el === target)) {
        setInput("");
        return value.concat(target);
      } else {
        return value;
      }
    };
    setForm(newValue());
  };

  return (
    <div className={styles.input_container}>
      <input
        list={label}
        type="text"
        name={label}
        value={input}
        onChange={handleChange}
        className={styles.form_input}
      />
      <datalist id={label}>
        {mapValues.map((el) => {
          const city = values[el].NAME;
          return (
            <Option
              value={city}
              label={city}
              key={nanoid()}
            />
          );
        })}
      </datalist>
      <label
        htmlFor={label}
        className={styles.form_label}>
        {label}
      </label>
    </div>
  );
};
