import classNames from "classnames";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUser } from "../../store/Users/Thunks/addUser";
import { FormElem } from "../FormElem/FormElem";
import { InputText } from "../../UI/InputText/InputText";
import { InputPhone } from "../../UI/InputPhone/InputPhone";
import { InputPass } from "../../UI/InputPass/InputPass";
import { InputRadio } from "../../UI/InputRadio/InputRadio";
import styles from "./styles.module.css";
import { isEmpty, isEmail, isMobilePhone } from "validator";
import { ROLES } from "../../assets/constants/Fixtires";
import { getCityFransheses } from "../../store/CityFranshises/Thunks/getCityFransheses";
import { selectCityFranshises } from "../../store/CityFranshises/selectors";
import { InputSelect } from "../../UI/InputSelect/InputSelect";

export const AddUserForm = ({ togglePopup }) => {
  const cityFran = useSelector(state => selectCityFranshises(state))
  const cityFranValues = Object.values(cityFran)
  const { userId, userIdAccess } = localStorage;
  const dispatch = useDispatch();
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [city, setCity] = useState(cityFranValues[0].ID);
  const [pass, setPass] = useState("");
  const [role, setRole] = useState(ROLES[0].id);
  const initialValidate = {
    validate: undefined,
    errorMessage: " ",
  };
  const [validate, setValidate] = useState(initialValidate);
  useEffect(() => {
    dispatch(getCityFransheses({userId}))
  }, [userId])
  useEffect(() => {
    if (validate.errorMessage.length < 5 && phone.length > 8) {
      dispatch(addUser({ userId, email, role, name, phone, pass }));
      togglePopup();
      resetForm();
    }
  }, [validate.errorMessage]);

  const resetForm = () => {
    setCity("");
    setEmail("");
    setName("");
    setPass("");
    setPhone("");
    setRole(ROLES[0].id);
  };
  const handleValidate = () => {
    if (
      isEmpty(email) ||
      isEmpty(phone) ||
      isEmpty(city) ||
      isEmpty(pass) ||
      isEmpty(name)
    ) {
      setValidate({
        isValid: false,
        errorMessage: "Пожалуйста, заполните все поля",
      });
    } else if (!isEmail(email)) {
      setValidate({
        isValid: false,
        errorMessage: "Неверный формат почты",
      });
    } else if (
      !isMobilePhone(phone) ||
      phone.length < 10 ||
      phone.length > 14
    ) {
      setValidate({
        isValid: false,
        errorMessage: "Неверный номер телефона",
      });
    } else setValidate({ isValid: true, errorMessage: "   " });
  };

  const onSubmit = (event) => {
    event.preventDefault();
    handleValidate();
  };

  return (
    <FormElem
      title={"Добавление пользователя"}
      onSubmit={onSubmit}>
      <InputText
        value={name}
        label={"Наименование"}
        setValue={setName}
      />
      <InputText
        value={email}
        label={"email"}
        setValue={setEmail}
      />
      <InputPhone
        value={phone}
        label={"Телефон"}
        setValue={setPhone}
      />
      <InputSelect mapValues={cityFranValues} setForm={setCity}/>
      {/* <InputText
        value={cityFran[city].NAME}
        label={"Город"}
        setValue={setCity}
      /> */}
      <InputPass
        value={pass}
        label={"Пароль"}
        setValue={setPass}
      />
      {(
        <>
          <span className={styles.radios_label}>Выберите роль:</span>
          <div className={styles.radios_container}>
            { ROLES.slice(userIdAccess == 1 ? 0 : userIdAccess).map((el) => {
              const { content, id } = el;
              return (
                <InputRadio
                  label={content}
                  value={id}
                  checked={role === id}
                  setValue={setRole}
                />
              );
            })}
          </div>
        </>
      )}
      <span className={styles.errorMessage}>{validate.errorMessage}</span>
      <button
        type="submit"
        className={classNames(styles.button, styles.form_submit)}>
        Добавить ползователя
      </button>
    </FormElem>
  );
};
