import { nanoid } from "nanoid";
import { useState } from "react";
import { Option } from "../Option/Option.tsx";
import styles from "./styles.module.css";

export const InputDataSelect = ({
  mapValues,
  setForm,
  size = 1,
  label,
  value,
}) => {
  const [list, setList] = useState([])

  const getStringFromArray = (arr) => {
    return arr.reduce((acc, el) => (acc + el + ', '), '')
  } 
  // const listValues = value.reduce((acc, el) => (acc + el + ', '), '')
  return (
    <div className={styles.input_container}>
      <input list={label} id={label} value={getStringFromArray()} onChange={setForm} size={size}></input>
      <datalist
        id={label}>
        {mapValues.map((el) => {
          return (
            <Option
              value={el.ID}
              label={el.NAME}
              key={nanoid()}
            />
          );
        })}
      </datalist>
      {/* <label
        htmlFor={label}
        className={styles.form_label__select}>
        {label}
      </label> */}
    </div>
  );
};

