import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  entities: { IDSRV: "", NAME: "", CITY: "", PHONE: "", FRAN_NAME: "" },
};

export const onjectFilterSlice = createSlice({
  name: "objectFilter",
  initialState,
  reducers: {
    setFilter: (state, action) => {
      const [entities, name] = action.payload;
      console.log(entities, name)
      return {
        ...state,
        [name]: state[name] + entities
      };
  },
    resetFilter: () => {
      return {
        initialState,
      };
    },
  },
});

export const onjectFilterSliceActions = onjectFilterSlice.actions;
