export const InputDataSelect = ({ value, setForm, label }) => {
  const [input, setInput] = useState("");
  const handleChange = (e) => {
    const target = e.target.value;
    setInput(target);
    const newValue = () => {
      if (mapValues.some((el) => el === target)) {
        setInput("");
        return value.concat(target);
      } else {
        return value;
      }
    };
    setForm(newValue());
  };

  return (
    <div className={styles.input_container}>
      <input
        list={label}
        type="text"
        name={label}
        value={input}
        onChange={handleChange}
        className={styles.form_input}
      />
      <datalist id={label}>
        {mapValues.map((el) => {
          
          return (
            <Option
              value={city}
              label={city}
              key={nanoid()}
            />
          );
        })}
      </datalist>
      <label
        htmlFor={label}
        className={styles.form_label}>
        {label}
      </label>
    </div>
  );
};
