import { nanoid } from "nanoid";
import { useState } from "react";
import styles from "./styles.module.css";
import { Option } from "../../UI/Option/Option.tsx";

export const InputDataSelect = ({ setForm, label, availableCities, cityFranIds, city }) => {
  const [input, setInput] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  function toggleIsOpen () {
    setIsOpen(!isOpen)
  }
  const handleChange = (e) => {
    const target = e.target.value;
    setInput(target);

    if (cityFranIds.includes(target)) {
      const newValue = city.concat(target);
      setForm(newValue);
      setInput('')
    }
  };

  return (
    <div className={styles.input_container}>
      <input
        // list={label}
        type="text"
        name={label}
        value={input}
        onClick={() => toggleIsOpen()}
        // onChange={handleChange}
        className={styles.form_input}
      />
      <div id={label} className={styles.optionContainer}>
        {availableCities.map((el) => {
          return (
            <Option
              value={el}
              label={el}
              key={nanoid()}
              onClick={handleChange}
            />
          );
        })}
      </div>
      <label
        htmlFor={label}
        className={styles.form_label}>
        {label}
      </label>
    </div>
  );
};
