import { nanoid } from "nanoid";
import { useState } from "react";
import { Option } from "../Option/Option.tsx";
import styles from "./styles.module.css";

export const InputDataSelect = ({
  mapValues,
  setForm,
  size = 1,
  label,
  value,
}) => {
  const [input, setInput] = useState('')
  
  return (
    
    <div className={styles.input_container}>
    <p>{value.map(el => <span>{el + ' '}</span>)}</p>
      <input list={label} id={label} value={input} onChange={(e) => setInput(e.target.value)} size={size} className={styles.form_input} />
      <datalist
        id={label}>
        {mapValues.map((el) => {
          return (
            <Option
              value={el.ID}
              label={el.NAME}
              key={nanoid()}
            />
          );
        })}
      </datalist>
    </div>
  );
};
